mapper.select:
select id, cat, content
 from cc_llm_kb
where cat=#{cat}

mapper.update:
update cc_agent_online
   set agent_status=#{status}
where opnum=#{opnum}

mapper.select:
select id, extnum, opnum, group_id groupId, session_id sessionId, agent_status agentStatus, last_hangup_time lastHangupTime, login_time loginTime, client_ip clientIp, skill_level skillLevel from cc_agent_online where 1=1
and group_id=#{groupId}
and agent_status=1 and group_id > 0 order by lastHangupTime asc

mapper.update:
update  cc_agent_online
   set agent_status=#{status}, busy_lock_time=#{busyLockTime}
where opnum=#{opnum}

update  cc_agent_online
   set   busy_lock_time = 0
where opnum=#{opnum}

update  cc_agent_online
   set  last_hangup_time = #{currentTime}
where extnum=#{extNum}


update  cc_agent_online
   set agent_status=1, busy_lock_time = 0
where busy_lock_time > 0 and opnum=#{opnum}  and busy_lock_time <=  #{expiredTimeMills}
